import React from 'react';
import './BuyFromFarmers.css'; // Import your CSS file for styling

const BuyFromFarmers = () => {
  const products = [
    {
      image: 'wheat.jpg', // Replace with your actual image path
      name: 'Wheat',
      price: '₹120/kg',
      quantity: '180kg'
    },
    {
      image: 'rice.jpg', // Replace with your actual image path
      name: 'Rice',
      price: '₹150/kg',
      quantity: '350kg'
    },
    {
      image: 'ricePlant.jpg', // Replace with your actual image path
      name: 'Rice Plant',
      price: '₹120/kg',
      quantity: '300kg'
    }
  ];

  return (
    <div className="buy-from-farmers">
      <div className="buy-from-farmers__top-right-image">
        {/* Image of a bag of wheat */}
      </div>
      <h2 className="buy-from-farmers__heading">Buy From Farmers</h2>
      <div className="buy-from-farmers__scrollable-cards">
        {products.map((product, index) => (
          <div key={index} className="buy-from-farmers__card">
            <img src={product.image} alt={product.name} className="buy-from-farmers__card-image" />
            <div className="buy-from-farmers__card-text">
              <h3>{product.name}</h3>
              <p>Price: {product.price}</p>
              <p>Quantity: {product.quantity}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyFromFarmers;
